s.time = d.getTime(); if (this.data.detail) &#123; this.data.snapshots.detail = this.data.detail; &#125; else &#123; this.data.snapshots.detail = &quot;暂无描述&quot;; &#125; &#125;, &#125;) &#125;, 选择要上传的图片12345678910111213141516171819202122232425262728293031323334353637//the rider choose an image from snapshots just takenchoose_image: function () &#123; if (this.data.files.length &gt;= 1) &#123; wx.showToast(&#123; title: &#x27;每位用户单个地点最多上传一张图片&#x27;, icon: &quot;none&quot; &#125;) return; &#125; var that = this; wx.chooseImage(&#123; //choose compressd image to get faster upload and save data sizeType: [&#x27;original&#x27;, &#x27;compressed&#x27;], count: 1, //take a snapshot or choose a photo sourceType: [&#x27;album&#x27;, &#x27;camera&#x27;], success: function (res) &#123; //check the size of the image var maxsize = 4000000; if (res.tempFiles[0].size &gt; maxsize) &#123; var original_size = (res.tempFiles[0].size / 1000000).toFixed(2); wx.showToast(&#123; title: &#x27;图片过大(&#x27; + original_size + &#x27;MB&#x27; + &#x27;)，请取消勾选&quot;原图&quot;或另行上传较小的图片&#x27;, icon: &#x27;none&#x27; &#125;) return; &#125; //return file path and attach to page data filepath array that.setData(&#123; files: that.data.files.concat(res.tempFilePaths), is_upload_add_hide: true, tip_second: &quot;长按图片删除&quot; &#125;); &#125; &#125;) this.choose_location();&#125;, 经过测试, 如果图片大小超过4Mib可能会造成微信闪退, 故加入了图片大小检测, 以提升系统的健壮性. 上传图片和备注123456789101112131415161718192021222324252627282930313233343536373839404142434445464748495051525354555657585960616263646566//upload image with location and detailupload_images: function () &#123; var that = this; //check if the user is uploading another snapshot to the same point for (var i = 0; i &lt; this.data.markers.length; i++) &#123; var marker = this.data.markers[i]; if (marker.openid == app.globalData.openid &amp;&amp; marker.name == this.data.snapshots.name) &#123; wx.showToast(&#123; icon: &#x27;none&#x27;, title: &#x27;每位用户单个地点最多上传一张图片&#x27;, &#125;) return; &